  {
    index: 1,
    title: "Second, you pick the flour",
    text: "another description",
    options: [ 
      {
        value: "Almond Flour",
        isChecked: "false"
      },
      {
        value:"Whole Wheat Flour",
        isChecked: "false"
      },
      {
        value:"Pastry Flour",
        isChecked: "false"
      }      
    ]
  }
];


