  constructor(props) {
    console.log("the props" + props);
    super(props);
    this.state = {
      cardData: [
        {
          index: 0,
          title: "First, you pick the oats",
          text: "description",
          selectedOption: 0,
          options: [ 
            {
              value: "Regular Oats",
