import React from 'react';
import logo from './logo.svg';
import './App.css';



const cardData =[
  {
    index: 0,
    title: "First, you pick the oats",
    text: "description",
    selectedOption: 0,
    options: [ 
      {
        value: "Regular Oats",
        isChecked: "false"
      },
      {
        value:"Quick Oats",
        isChecked: "false"
      },
      {
        value:"Steel Cut Oats",
        isChecked: "false"
      }      
    ]
  },
  {
    index: 1,
    title: "Second, you pick the flour",
    text: "another description",
    options: [ 
      {
        value: "Almond Flour",
        isChecked: "false"
      },
      {
        value:"Whole Wheat Flour",
        isChecked: "false"
      },
      {
        value:"Pastry Flour",
        isChecked: "false"
      }      
    ]
  }
];

const selectedOptions = []

class SelectedOptions extends React.Component {
  constructor(props) {
    super(props);
    this.state = {selectedOption: []}

    this.handleSelection = this.handleSelection.bind(this);
  }

  handleSelection(props) {
    console.log(this.state.selectedOption)
    this.setState({

    })
  }
}


class Card extends React.Component {
  constructor(props) {
    super(props);
    this.handleNextClick = this.handleNextClick.bind(this);
    this.state = {currentCard: 0}
  }

  handleNextClick() {
    console.log(this.state.currentCard)
    this.setState({
      currentCard: this.state.currentCard + 1
    })
    console.log("state updated!");
    //get selected options

    //pass to handleSeletion
  }


  render() {
    const currentCard = this.state.currentCard;
    return (
      <div class="row justify-content-sm-center">
        <div class="col-3"></div>
        <div class="col-3">
          <div class="card text-center survey-card">
            <div class="card-title">
              {cardData[currentCard].title}
            </div>
            <div class="card-body">
              <Options c={currentCard}/>
            </div>
          </div>
        </div>
        <div class="col-3">
        <NextButton onClick={this.handleNextClick}/>
        </div>
      </div>
    )
  }

}


function NextButton(props) {
  return (
    <button type="button" class="btn btn-outline-primary" onClick={props.onClick}>Next</button>
  )
}

function Options(props) {
  var currentOptions = cardData[props.c].options;
  var options = currentOptions.map((option) =>
    <div class="form-check">
      <input class="form-check-input" type="checkbox" value={option} unchecked= "true" id="defaultCheck1"></input>
      <label class="form-check-label" for="defaultCheck1">{option}</label>
    </div>
  );
  return (
    <div class="form-group">
      {options}
    </div>
  )
}



function App() {

  return (

    <div class="container">
      <div class="row justify-content-sm-center">
        <div class="col-4 title">
          BearBaking Cookie Guide
        </div>
      </div>
      <Card />
    </div>
    // <div className="App">
    //   <header className="App-header">
    //     <img src={logo} className="App-logo" alt="logo" />
    //     <p>
    //       Edit <code>src/App.js</code> and save to reload.
    //     </p>
    //     <a
    //       className="App-link"
    //       href="https://reactjs.org"
    //       target="_blank"
    //       rel="noopener noreferrer"
    //     >
    //       Hello world!
    //     </a>
    //   </header>
    // </div>
  );

}

export default App;
