import React from 'react';
import logo from './logo.svg';
import './App.css';



const cardData =[
  {
    index: 0,
    title: "First, you pick the oats",
    text: "description",
    selectedOption: 0,
    options: [ 
      "Regular Oats",
      "Quick Oats",
      "Steel Cut Oats"
    ]
  },
  {
    index: 1,
    title: "Second, you pick the flour",
    text: "another description",
  }
];


class Options extends React.Component {
  constructor(props) {
    super(props);
    this.state = {selectedOption: []}

    this.handleSelection = this.handleSelection.bind(this);
  }

  handleSelection() {
    console.log(this.state.selectedOption)
    this.setState({

    })
  }
}


class Card extends React.Component {
  constructor(props) {
    super(props);
    this.handleNextClick = this.handleNextClick.bind(this);
    this.state = {currentCard: 0}
  }

  handleNextClick() {
    console.log(this.state.currentCard)
    this.setState({
      currentCard: this.state.currentCard + 1
    })
    console.log("state updated!");
  }

  render() {
    const currentCard = this.state.currentCard;
    return (
      <div class="row justify-content-sm-center">
        <div class="col-3"></div>
        <div class="col-3">
          <div class="card text-center survey-card">
            <div class="card-body">
              {cardData[currentCard].title}
            </div>
          </div>
        </div>
        <div class="col-3">
        <NextButton onClick={this.handleNextClick}/>
        </div>
      </div>
    )
  }

}


function NextButton(props) {
  return (
    <button type="button" class="btn btn-outline-primary" onClick={props.onClick}>Next</button>
  )
}



function App() {

  return (

    <div class="container">
      <div class="row justify-content-sm-center">
        <div class="col-4 title">
          BearBaking Cookie Guide
        </div>
      </div>
      <Card />
    </div>
